package javaBasic;

public class Topic_03_Compare {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 1 vùng nhớ cho biến x
		int x = 1;
		
		// 1 vùng nhớ cho biến y
		int y = x;
		
		System.out.println("x = " + x);
		System.out.println("y = " + y);

		y = 10;
		
		System.out.println("x = " + x);
		System.out.println("y = " + y);
	}

}
